import querystring from "querystring";
// import Buffer

import Buffer from "buffer";


const NOW_PLAYING_ENDPOINT = `https://api.spotify.com/v1/me/player/currently-playing`;
const TOKEN_ENDPOINT = `https://accounts.spotify.com/api/token`;

// const client_id = "0c99499a79144a53a75b7c51a9b8c4c9";
//  const client_secret = "d81ef045e9334706b3c59ecfa35874d3";
const refresh_token = "AQCNTV33SgFKs6rtJ8hdEmy7n6ZUsyxVhEO_hKxNAAzasROY5akonwzacz1kgXTFYyqAEneAOiVWdFaWMuUPPZckTcEWqMGmJku81mMXn8fDOCW5CQBEPPgB21Lh18Tgy1M";

const getAccessToken = async () => {
  const basic = Buffer.from(`0c99499a79144a53a75b7c51a9b8c4c9:d81ef045e9334706b3c59ecfa35874d3`).toString("base64");

  const response = await fetch(TOKEN_ENDPOINT, {
    method: "POST",
    headers: {
      Authorization: `Basic ${basic}`,
      "Content-Type": "application/x-www-form-urlencoded",
    },
    body: querystring.stringify({
      grant_type: "refresh_token",
      refresh_token,
    }),
  });

  return response.json();
};

export const getNowPlaying = async (client_id, client_secret, refresh_token) => {
  const { access_token } = await getAccessToken(
    client_id,
    client_secret,
    refresh_token
  );

  return fetch(NOW_PLAYING_ENDPOINT, {
    headers: {
      Authorization: `Bearer ${access_token}`,
    },
  });
};

export default async function getNowPlayingItem(
  client_id,
  client_secret,
  refresh_token
) {
  const response = await getNowPlaying(client_id, client_secret, refresh_token);
  if (response.status === 204 || response.status > 400) {
    return false;
  }

  const song = await response.json();
  const albumImageUrl = song.item.album.images[0].url;
  const artist = song.item.artists.map((_artist) => _artist.name).join(", ");
  const isPlaying = song.is_playing;
  const songUrl = song.item.external_urls.spotify;
  const title = song.item.name;

  return {
    albumImageUrl,
    artist,
    isPlaying,
    songUrl,
    title,
  };
}
